import React from 'react'
import { connect } from 'react-redux'
import {addTask} from "../../store/actions/taskActions";

const CreateTask = ({ onClick }) => {
    return(
        <div className="card task-summary">
            <div className="card-content black-text text-darken-3">
                <label htmlFor={'title'}>Title</label>
                <input type={'text'} name={'title'} />
                <button className={'btn btn-small waves-effect waves-light'} onClick={ () => onClick({title: 'new task!'})}>yooooo</button>
            </div>
        </div>
    );
}

const mapDispatchToProps = (dispatch) => {
    return {
        onClick: (newTask) =>  {
            dispatch(addTask(newTask))
        }
    }
}

export default connect(null, mapDispatchToProps)(CreateTask)
