import initialData from "../../initial-data";

const rootReducer = ( state = initialData, action) => {
    console.log('action', action)
    switch(action.type) {
        case 'CREATE_TASK':
            const newTasks = {
                ...state.tasks,
                [action.content.id]: {
                    id: action.content.id,
                    content: action.content.taskInfo.task,
                }
            };

            const taskList = state.columns[action.content.taskInfo.columnId].taskIds;
            const newColumns = {
                ...state.columns,
                [action.content.taskInfo.columnId]: {
                    id: action.content.taskInfo.columnId,
                    title: action.content.taskInfo.title,
                    tasksIds: [...taskList, action.content.id]
                }
            }
            return {
                ...state,
                tasks: newTasks,
                columns: newColumns,
            }
        case 'UPDATE_BOARD':
            return {
                tasks: action.board.tasks,
                columns: action.board.columns,
                columnOrder: action.board.columnOrder,
            }
        default:
            return state;
    }
    return state;
}

export default rootReducer
