import React, { useState } from 'react'
import TaskList from "../tasks/TaskList";
import { connect } from 'react-redux'

const Dashboard = (props) => {

    const [ todo, setTodo ] = useState([{title: 'make bed'}, {title: 'walk the dog'}]);

    const [ progress, setProgress ] = useState([{title: 'abc'}, {title: 'cbd'}]);

    const [ done, setDone ] = useState([{title: 'thc'}, {title: 'weed'}]);


    const handleTodo = (newTask) => {
        setTodo([
            ...todo,
            {title: newTask.title}
        ])
    }

    const handleProgress = (newTask) => {
        setProgress([
            ...progress,
            {title: newTask.title}
        ])
    }

    const handleDone = (newTask) => {
        setDone([
            ...done,
            {title: newTask.title}
        ])
    }

    return (
      <div className="container dashboard">
          <div className="row">
              <div className="col s7 m3 offset-m1" >
                  <h5>todo</h5>
                  <TaskList list={todo} onClick={handleTodo}/>
              </div>
              <div className="col s7 m3 offset-m1">
                  <h5>in progress</h5>
                  <TaskList list={progress} onClick={handleProgress}/>
              </div>
              <div className="col s7 m3 offset-m1">
                  <h5>done</h5>
                  <TaskList list={done} onClick={handleDone}/>
              </div>
          </div>
      </div>
    );
}

const mapStateToProps = (state) => {
    return {
        todos: state.todos,
        progress: state.progress,
        done: state.done,
    }
}

export default connect(mapStateToProps)(Dashboard)
