import React from 'react'

const initState = {
    todos: [ {title: 'setup redux'}],
    progress: [ {title: 'finish css'}],
    done: [ {title: 'deploy app'}]
}

const rootReducer = (state = initState , action) => {
    if (action === 'ADD_TASK')
    {
        state.todos = [...state.todos, action.value];
        console.log(state);

    }
    return state;
}

export default rootReducer
