import React from 'react'

const initState = {
    todos: [ {title: 'setup redux'}],
    progress: [ {title: 'finish css'}],
    done: [ {title: 'deploy app'}]
}

const rootReducer = (state = initState , action) => {
    console.log(action)
    switch (action.type) {
        case 'ADD_TASK':
            state.todos.push({title: action.taskTitle})
            console.log(state.todos)
            return state;
        default:
            return state;
    }
    // if (action.type === 'ADD_TASK')
    //     state.todos.push(action.content);
}

export default rootReducer
