import initialData from "../../initial-data";

const rootReducer = ( state = initialData, action) => {
    console.log('initial data', initialData)
    switch(action.type) {
        case 'CREATE_TASK':
            const { tasks } = state;
            const newTasks = [...tasks];
            return {
                ...state,
                tasks: newTasks,
            }
        default:
            return state;
    }
    return state;
}

export default rootReducer
