import initialData from "../../initial-data";

const rootReducer = ( state = initialData, action) => {
    console.log('action', action)
    switch(action.type) {
        case 'CREATE_TASK':
            const { tasks } = state;
            const newTasks = {
                ...state.tasks,
                [action.content.id]: {
                    id: action.content.id,
                    content: action.content.taskInfo.task,
                }
            };
            return {
                ...state,
                tasks: newTasks,
            }
        default:
            return state;
    }
    return state;
}

export default rootReducer
