import React, { useState } from 'react'
import TaskList from "../tasks/TaskList";


const Dashboard = (props) => {

    const [ todo, setTodo ] = useState([{title: 'make bed'}, {title: 'walk the dog'}]);

    const [ progress, setProgress ] = useState([{title: 'abc'}, {title: 'cbd'}]);

    const [ done, setDone ] = useState([{title: 'thc'}, {title: 'weed'}]);


    const handleTodo = (newTodo) => {
        setTodo([
            ...todo,
            {title: newTodo.title}
        ])
    }

    return (
      <div className="container dashboard">
          <div className="row">
              <div className="col s7 m3 offset-m1" >
                  <h5>todo</h5>
                  <TaskList list={todo} onClick={setTodo}/>
              </div>
              <div className="col s7 m3 offset-m1">
                  <h5>in progress</h5>
                  <TaskList list={progress} onClick={setProgress}/>
              </div>
              <div className="col s7 m3 offset-m1">
                  <h5>done</h5>
                  <TaskList list={done} onClick={setDone}/>
              </div>
          </div>
      </div>
    );
}

export default Dashboard
