let taskID = 100;

export const createTask = (task) => {
    const obj = {
        type: 'CREATE_TASK',
        id: `task-${++taskID}`,
        taskInfo: task
    }
   return obj;
}

export const updateBoard = (board) => {
    return {
        type: 'UPDATE_BOARD',
        board,
    }
}
