import React from 'react'

const initState = {
    todos: [ {title: 'setup redux'} ],
    progress: [ {title: 'finish css'} ],
    done: [ {title: 'deploy app'} ]
}

const rootReducer = (state = initState , action) => {
    console.log('reducer' , state, action)
    switch (action.type) {
        case 'ADD_TASK':
            const newState = [ ...state.todos, { title: action.taskTitle }];
            state.todos = newState;
            return state;
        default:
            return state;
    }
}

export default rootReducer
