let taskID = 100;

export const createTask = (task) => {
   return {
       type: 'CREATE_TASK',
       content: {
           id: `task-${++taskID}`,
           taskInfo: task
       }
   }
}

export const updateBoard = (board) => {
    return {
        type: 'UPDATE_BOARD',
        board,
    }
}
