import initialData from "../../initial-data";

const rootReducer = ( state = initialData, action) => {
    switch(action.type) {
        case 'CREATE_TASK':
            const { tasks, columns } = state;
            const newTasks = Array.from(tasks);
            return {
                ...state,
                tasks: newTasks,
            }
        default:
            return state;
    }
    return state;
}

export default rootReducer
